(function($) {
	$.fn.extend({
		leanModal : function(options) {
			var defaults = {
			    title:'',
				top : 10,
				overlay : 0.5,
				closeButton : null
			};
			var overlay = $("<div id='lean_overlay'></div>");
			$("body").append(overlay);
			options = $.extend(defaults, options);
			return this.each(function() {
				var o = options;
				$(this).click(function(e) {
					var modal_id = $(this).attr("href");
					$("#lean_overlay").click(function() {
						close_modal(modal_id)
					});
					
					var modal_height = $(modal_id).outerHeight();
					var modal_width = $(modal_id).outerWidth();
					$("#lean_overlay").css({
						"display" : "block",
						opacity : 0
					});
					$("#lean_overlay").fadeTo(200, o.overlay);
					$(modal_id).css({
						"display" : "block",
						"position" : "fixed",
						"opacity" : 0,
						"z-index" : 2221,
						"left" : 50 + "%",
						"margin-left" : -(modal_width / 2) + "px",
						"top" : o.top + "px"
					});
					
					$(modal_id).addClass("openWindow");
					
					var head = "<div id=\"OpenWindow-header\"><b>"+o.title+"</b><a href=\"###\" class=\"modal_close\"></a></div>";
					
					
					$(modal_id).prepend(head);
					
					$(o.closeButton).click(function() { 
						close_modal(modal_id);
					}); 
					
					$(modal_id).fadeTo(200, 1);
					e.preventDefault()
				})
			});
			function close_modal(modal_id) {
				$("#lean_overlay").fadeOut(200);
				$(modal_id).css({
					"display" : "none"
				}) 
			    $(modal_id).find("#OpenWindow-header").remove(); 
				//隐藏验证提示框   
				//$(modal_id).find("form").validationEngine('hide');
				
			}
		}
	})
})(jQuery);
